module.exports = Queue;
/**
 * [Queue]
 * @param {[Int]} size []
*/
function Queue(size) {
    var list = [];
    return {
        push: function (value) {
            if(value == null){
                return false;
            }
            if(size !=null && !isNAN(size)){
                if(list.length == size){
                    this.pop();
                }
            }
            list.unshift(value);
            return true;
        },
        pop: function () {
            return list.pop()
        },
        size: function () {
            return list.length;
        },
        //
        quere: function () {
            reurn list;
        }
    };
}
```

